// n8n Code node (JavaScript)
// Input: items with item.json.output (string)
// Output: items with item.json.output formatted for Arabic RTL

const items = $input.all();

// --- helpers ---
const ARABIC_COMMA = '\u060C';
const ARABIC_SEMI  = '\u061B';
const ARABIC_QMARK = '\u061F';
const RLM = '\u200F';   // Right-to-Left Mark
const LRM = '\u200E';   // Left-to-Right Mark
const RLI = '\u2067';   // Right-to-Left Isolate
const PDI = '\u2069';   // Pop Directional Isolate

const digitMap = {
  '0':'\u0660','1':'\u0661','2':'\u0662','3':'\u0663','4':'\u0664',
  '5':'\u0665','6':'\u0666','7':'\u0667','8':'\u0668','9':'\u0669'
};

function toArabicIndicDigits(str) {
  return str.replace(/[0-9]/g, d => digitMap[d]);
}

function saveUrls(text) {
  const urls = [];
  const placeholder = (i) => `__URL_PLACEHOLDER_${i}__`;
  const urlRe = /(https?:\/\/[^\s)\]}<>]+)(?=$|\s|[\])}>])/g;
  let i = 0;
  text = text.replace(urlRe, (m) => {
    urls.push(m);
    return placeholder(i++);
  });
  return { text, urls, placeholder };
}

/* ====== NEW: smarter restore that pastes real links ====== */

// Arabic digits â†’ ASCII "0-9"
const arabicDigitToAscii = (s) =>
  s.replace(/[\u0660-\u0669]/g, (d) => String('Ù Ù¡Ù¢Ù£Ù¤Ù¥Ù¦Ù§Ù¨Ù©'.indexOf(d)));

// Decode alphabetic tokens: A->0, B->1 ... Z->25, AA->26 ...
function alphaTokenToIndex(tok) {
  let n = 0;
  for (let i = 0; i < tok.length; i++) {
    n = n * 26 + (tok.charCodeAt(i) - 65 + 1);
  }
  return n - 1;
}

/**
 * Restores URLs into text:
 * - Restores the formatterâ€™s own placeholders: snap.placeholder(i)
 * - Replaces "__URL_PLACEHOLDER_0__" (and Arabic digits "__..._Ù¡__")
 * - Replaces "__URL_PH_A__" / "__URL_PH_AB__"
 * - Pulls URLs from:
 *    - snap.urls (auto-captured from text)
 *    - item.json.urls (array)
 *    - item.json.sources[].url / .link
 *    - item.json.web_sources[].url / .link
 */
function restoreUrlsSmart(text, snap, item) {
  // 1) Build a candidate list of URLs in priority order
  const all = [];

  // a) URLs auto-captured by saveUrls (from the text itself)
  snap.urls.forEach(u => all.push(u));

  // b) Flat array item.json.urls
  if (Array.isArray(item?.json?.urls)) {
    for (const u of item.json.urls) {
      if (typeof u === 'string' && /^https?:\/\//.test(u)) all.push(u);
    }
  }

  // c) Structured sources arrays
  const buckets = [item?.json?.sources, item?.json?.web_sources];
  for (const arr of buckets) {
    if (Array.isArray(arr)) {
      for (const s of arr) {
        const u = s?.url || s?.link;
        if (typeof u === 'string' && /^https?:\/\//.test(u)) all.push(u);
      }
    }
  }

  // 2) Replace the formatterâ€™s own saved placeholders first
  //    (snap.placeholder(i) == "__URL_PLACEHOLDER_i__" used internally)
  for (let i = 0; i < snap.urls.length; i++) {
    const needle = snap.placeholder(i); // "__URL_PLACEHOLDER_i__"
    const safe = `${LRM}${snap.urls[i]}${LRM}`;
    text = text.split(needle).join(safe);
  }

  // 3) Replace external numeric placeholders: "__URL_PLACEHOLDER_0__" or "__..._Ù¡__"
  text = text.replace(/__URL_PLACEHOLDER_([0-9\u0660-\u0669]+)__/g, (m, num) => {
    const idx = parseInt(arabicDigitToAscii(num), 10);
    const u = all[idx];
    return u ? `${LRM}${u}${LRM}` : m;
  });

  // 4) Replace alphabetic placeholders: "__URL_PH_A__", "__URL_PH_AB__", etc.
  text = text.replace(/__URL_PH_([A-Z]+)__/g, (m, token) => {
    const idx = alphaTokenToIndex(token);
    const u = all[idx];
    return u ? `${LRM}${u}${LRM}` : m;
  });

  // 5) Ensure each URL is on its own line
  text = text.replace(/\s+(https?:\/\/)/g, '\n$1');

  return text;
}
/* ====== END new restore ====== */

function normalizePunctuation(s) {
  return s
    .replace(/,/g, ARABIC_COMMA)
    .replace(/;/g, ARABIC_SEMI)
    .replace(/\?/g, ARABIC_QMARK);
}

function normalizeWhitespace(s) {
  // Normalize line breaks and collapse blank lines
  s = s.replace(/\r\n/g, '\n').replace(/\r/g, '\n');
  s = s.replace(/[ \t]+\n/g, '\n');      // trim trailing spaces
  s = s.replace(/\n{3,}/g, '\n\n');      // collapse >2 blank lines
  s = s.replace(/[ \t]{2,}/g, ' ');      // collapse multiple spaces
  return s.trim();
}

function formatBulletLine(content) {
  // Ensure each bullet is on its own line with proper RTL formatting and spacing
  content = content.trim();
  return `\n${RLI}â€¢${RLM} ${content}${PDI}`;
}

function formatNormalLine(content) {
  // Wrap non-bullet Arabic lines in RTL isolate for correct punctuation
  content = content.trim();
  return content ? `${RLI}${content}${PDI}` : '';
}

function formatSectionHeader(content) {
  // Format section headers with extra spacing
  content = content.trim();
  return content ? `\n\n${RLI}${content}${PDI}\n` : '';
}

function convertListMarkers(line) {
  // Convert common list markers to unified "â€¢"
  // e.g., "-", "*", "â€“", "â€”", "Â·", "â€¢" -> "â€¢"
  const m = line.match(/^\s*([-*â€“â€”Â·â€¢])\s+(.*)$/);
  if (m) return { isBullet: true, body: m[2] };
  // Also support ordered items like "1) text" or "Ù¡) Ù†Øµ"
  const ordered = line.match(/^\s*([0-9\u0660-\u0669]+)[\.\)]\s+(.*)$/);
  if (ordered) return { isBullet: true, body: ordered[2] };
  return { isBullet: false, body: line };
}

function arabizeOrderedPrefix(line) {
  // If the line starts with "1) " etc. inside "Ø§Ù„Ù…ØµØ§Ø¯Ø±:" block, convert to Arabic-Indic
  return line.replace(/^\s*([0-9]+)\)\s/, (m, d) => `${toArabicIndicDigits(d)}) `);
}

function formatSourcesBlock(lines, startIdx) {
  // Ensure Arabic-Indic numbering (Ù¡)ØŒ (Ù¢)ØŒ (Ù£) and keep URL on its own next line.
  for (let i = startIdx + 1; i < lines.length; i++) {
    if (!lines[i].trim()) continue;
    const l = lines[i];
    // Numbered item
    if (/^\s*([0-9]+)\)\s+/.test(l) || /^\s*([\u0660-\u0669]+)\)\s+/.test(l)) {
      const numbered = arabizeOrderedPrefix(l);
      lines[i] = formatNormalLine(numbered);
      continue;
    }
    // URL line: keep LTR, but wrap with isolates for overall layout
    if (/^https?:\/\//.test(l.trim())) {
      lines[i] = `${RLI}${LRM}${l.trim()}${LRM}${PDI}`;
      continue;
    }
    // A regular line in sources block (e.g., title line). Keep RTL isolate.
    lines[i] = formatNormalLine(l);
  }
}

const formattedItems = items.map((item) => {
  let text = String(item?.json?.output ?? '');

  // 1) Normalize whitespace early
  text = normalizeWhitespace(text);

  // 2) Temporarily remove URLs so digits/punct changes don't affect them
  const snap = saveUrls(text);
  text = snap.text;

  // 3) Punctuation -> Arabic variants
  text = normalizePunctuation(text);

  // 4) Arabic-Indic digits (skip URLs because we removed them)
  text = toArabicIndicDigits(text);

  // 5) Line-by-line formatting (bullets, isolates, sources)
  let lines = text.split('\n');
  let formattedLines = [];

  // Detect "Ø§Ù„Ù…ØµØ§Ø¯Ø±:" block to treat numbering/URLs nicely
  let sourcesIdx = lines.findIndex(l => l.trim().startsWith('Ø§Ù„Ù…ØµØ§Ø¯Ø±:'));

  // Process each line and ensure proper line breaks and spacing
  lines.forEach((line, idx) => {
    const raw = line.trim();

    if (!raw) {
      // Empty line - add as is
      formattedLines.push('');
      return;
    }

    // Keep the exact "Ø§Ù„Ù…ØµØ§Ø¯Ø±:" label as RTL
    if (raw === 'Ø§Ù„Ù…ØµØ§Ø¯Ø±:') {
      formattedLines.push(`\n\n${RLI}Ø§Ù„Ù…ØµØ§Ø¯Ø±:${PDI}\n`);
      return;
    }

    // Check if this is a section header (like "Ø§Ù„Ù…Ù‡Ø§Ù…:" or "Ø§Ù„Ù‡ÙŠÙƒÙ„ÙŠØ©:")
    if (raw.endsWith(':') && !raw.includes('â€¢') && raw.length < 20) {
      formattedLines.push(formatSectionHeader(raw));
      return;
    }

    // Convert list markers to unified bullet
    const { isBullet, body } = convertListMarkers(line);

    if (isBullet) {
      formattedLines.push(formatBulletLine(body));
    } else {
      // Regular paragraph/line - keep definition text as is
      formattedLines.push(formatNormalLine(raw));
    }
  });

  // If there is a sources block, tidy its subsequent lines
  if (sourcesIdx !== -1) {
    formatSourcesBlock(formattedLines, sourcesIdx);
  }

  text = formattedLines.join('\n');

  // 6) Restore URLs with real links (NEW)
  text = restoreUrlsSmart(text, snap, item);

  // 7) Final cleanup: remove redundant isolates and ensure proper spacing
  text = text.replace(new RegExp(`${RLI}${PDI}`, 'g'), '');

  // Ensure proper spacing between sections and bullet points
  text = text.replace(/(\n)(\u2067[^â€¢])/g, '$1\n$2'); // Add space before new sections
  text = text.replace(/(â€¢[^\n]+\u2069)(\u2067[^â€¢])/g, '$1\n\n$2'); // Add space after bullet lists

  // Clean up multiple blank lines but maintain good spacing
  text = text.replace(/\n{4,}/g, '\n\n\n');

  return { json: { output: text.trim() }, pairedItem: item.pairedItem };
});

return formattedItems;
