// Get the JSON object from previous node output
const data = items[0].json.output;

// Convert object to text "key: value" format
let textOutput = '';
for (const [key, value] of Object.entries(data)) {
  textOutput += `${key}: ${value}\n`;
}

// Return the text in a field for next nodes
return [{
  json: {
    text: textOutput.trim()
  }
}];
